function scrollScreen(){var e=document.documentElement.clientHeight;document.documentElement.clientWidth<576&&window.scrollTo({left:0,top:e,behavior:"smooth"})}document.addEventListener("scroll",function(){document.documentElement.clientHeight/10<=document.documentElement.scrollTop?document.querySelector(".screen-down").classList.add("screen-down--hide"):document.querySelector(".screen-down").classList.remove("screen-down--hide")});